import React, {Component} from 'react';
import './app.css';
import Nav from './components/nav/Nav';
import Home from './components/home/Home';
import About from './components/about/About';
import Posts from './components/posts/Posts';
import { BrowserRouter as Router, Route } from 'react-router-dom';

// 把開頭第一個字元 "#" 拿掉
const removeHashSymbol = str => {
  return str.slice(1);
}

class App extends Component {
  constructor() {
    super()
    this.state = {
      tab: removeHashSymbol(window.location.hash) || 'home'
      //tab: 'posts', //預設
    }
  }

  componentDidMount() {
    window.addEventListener('hashchange', this.handleHashChange)
  }

  // 把 eventListener 拿掉
  componentWillUnmount(prevProps, prevState) {
    window.removeEventListener(this.handleHashChange);
  }

  handleHashChange = () => {
    this.setState({
      tab: removeHashSymbol(window.location.hash)
    })
    // alert(window.location.hash);
  }

  // onNavChange = page => {
  //   this.setState({
  //     tab: page
  //   })
  // }

  render() {
    const {tab} = this.state;
    return (
      <Router>
        <div>
          <Nav onChange={this.onNavChange} activeTab={tab}/>

          <Route exact path="/" component={Home} />
          <Route path="/posts" component={Posts} />
          <Route path="/about" component={About} />

          {tab === 'home' && <Home />}
          {tab === 'posts' && <Posts />}
          {tab === 'about' && <About />}
        </div>
      </Router>
    )
  }
}

export default App;
