import React, {Component} from 'react';
import {withRouter} from 'react-router-dom'; //Higher order component
import axios from 'axios'
import './posts.css'

class PostList extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
    }
  }

  componentDidMount() {
    // axios.get('https://jsonplaceholder.typicode.com/posts')
    // axios.get('https://qootest.com/posts')
    // .then(response => {
    //     this.setState({
    //       posts: response.data
    //     })
    //   })

    axios.get('https://api.unsplash.com/photos/?client_id=' + cred.APP_ID)
    .then(response => {
        this.setState({
          posts: response.data
        })
      })



  }

  render() {
    const {posts} = this.state;
    const {history} = this.props;
    return (
      <div>
        <h1>Posts</h1>

          <div className="container">
            <div className="row post-list">
              {posts.map(item => (
                <div className="post-item col-md-3 col-sm-6 mb-5" key={item.id} onClick={() => {
                  history.push('/post/' + item.id); //一個換頁的 function
                  // this.setState({
                  //   postId: item.id
                  // })
                }}>
                  <div className="post-item__id">
                    {item.id}
                  </div>
                  {item.title}
                </div>
              ))}
            </div>
        </div>

    </div> 
    )
  }
}

// export default PostList;
export default withRouter(PostList);
