import React, {Component} from 'react';
import {withRouter} from 'react-router-dom'; //Higher order component
import axios from 'axios'
import './posts.css';

class PostList extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
      images: []
    }
  }

  componentDidMount() {
    axios.get('https://qootest.com/posts')
    .then(response => {
        this.setState({
          posts: response.data
        })
      })

   //axios.get('https://api.unsplash.com/search/photos?query=hipster&per_page=10&client_id=d55585de313318197beb6b2a38c854be127c4d1af43afa578ab3db7437c2d356')
   axios.get('https://api.unsplash.com/search/photos?query=hipster&per_page=100&client_id=d55585de313318197beb6b2a38c854be127c4d1af43afa578ab3db7437c2d356')
   .then(response=> {
      //console.log(response.data.results);
      this.setState({ 
        images: response.data.results 
        })
      }
    )
  }

  render() {
    const {posts} = this.state;
    const {history} = this.props;
    const {images} = this.state;
    return (
      <div>

        <div className="post-item col-md-8 mx-auto">
          {images[1]}
          {/* <img src={images[0].urls.small}  /> */}
        </div>


              {/* {posts.slice(0, 10).map((item, i)=> (
                 <div className="post-item col-md-3 col-sm-6 mb-5" key={item.id} onClick={() => {
                   history.push('/post/' + item.id); //一個換頁的 function
                 }}>
                   <div className="post-item__id">
                     {item.id}
                   </div>
                   {item.title} 
                    <img src={images[i].urls.small} width="200px" />
                 </div>
               ))} */}

           <div className="container mt-5">
            <div className="row post-list">     
               {posts.map((item)=> (
                 <div className="post-item col-md-8 mx-auto" key={item.id} onClick={() => {
                   history.push('/post/' + item.id); //一個換頁的 function
                 }}>
                   <div className="post-item__id">
                     {item.id}
                   </div>
                   {item.title} 
                    {/* <img src={images.urls.small} /> */}
                 </div>
               ))}
            </div>
          </div>



          {/* <div className="container">
            <div className="row post-list">
              { images.map(item => (
                <div>
                  <img src={item.urls.small} />
                </div>
              )) }
            </div>
          </div> */}

    </div> 
    )
  }
}

// export default PostList;
export default withRouter(PostList);
