import React, {Component} from 'react';
import {withRouter} from 'react-router-dom'; //Higher order component
import axios from 'axios'
import './posts.css';
import ImageList from './imageList';

class PostList extends Component {
  constructor() {
    super();
    this.state = {
      // posts: [],
      images: []
    }
  }

  componentDidMount() {
    //axios.get('https://jsonplaceholder.typicode.com/posts')

    /*
    axios.get('https://qootest.com/posts')
    .then(response => {
        this.setState({
          posts: response.data
        })
      })
    */
   axios.get('https://api.unsplash.com/search/photos', {
    params: { query: term},
    headers: {
        Authorization: 'd55585de313318197beb6b2a38c854be127c4d1af43afa578ab3db7437c2d356'
    }
    }).then(response=> {
        this.setState({ images: response.data.results })
      }
    )

  }

  render() {
    const {posts} = this.state;
    const {history} = this.props;
    return (
      <div>
        <h1>Posts</h1>

          <div className="container">
            <div className="row post-list">
              {posts.map(item => (
                <div className="post-item col-md-3 col-sm-6 mb-5" key={item.id} onClick={() => {
                  history.push('/post/' + item.id); //一個換頁的 function
                  // this.setState({
                  //   postId: item.id
                  // })
                }}>
                  <div className="post-item__id">
                    {item.id}
                  </div>
                  {item.title} 
                </div>
              ))}
            </div>
        </div>

    </div> 
    )
  }
}

// export default PostList;
export default withRouter(PostList);
