import React, {Component} from 'react';
import { Link, Route } from 'react-router-dom';

class Item extends Component {
  render() { 
    const {to, children} = this.props;

    return ( 
      <Route
        path={to}
        exact={false}
        children={({ match }) => (
          <li className={match ? "nav-link active" : "nav-link"}>
            <Link to={to} className="nav-link">{children}</Link>
          </li>
        )}
    />

      // <li className="nav-item active" >
      //   {/* <a className="nav-link" href={'#' + name}>{children}</a> */}
      //   <Link to={'/' + name} className="nav-link">
      //     {children}
      //   </Link>
      // </li>
    );
  }
}

class Nav extends Component {
  render() {
    return (
      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="">Navbar</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <Item to="/" exatc={true}>
                Home
              </Item>
              <Item to="/posts">
                Posts
              </Item>
              <Item to="/about">
                About
              </Item>

              {/* <li className={`nav-item ${activeTab === 'articles' && 'active'} `} onClick={() => onChange('articles')}>
                <a className="nav-link" href="#">Articles</a>
              </li>
              <li className={`nav-item ${activeTab === 'about' && 'active'} `} onClick={() => onChange('about')}>
                <a className="nav-link" href="#">About</a>
              </li> */}

            </ul>
          </div>
        </nav>
      </div>
    )
  }
}

export default Nav;
