import React, {Component} from 'react';
import './posts.css';

class Posts extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
      postId: null,
      post: {}
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(response => response.json())
    .then(json => this.setState({
      posts: json
    }))
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.postId !== this.state.postId) {
      this.getPost();
    }
  }

  getPost = () => {
    fetch('https://jsonplaceholder.typicode.com/posts/' + this.state.postId)
    .then(response => response.json())
    .then(json => this.setState({
      post: json
    }))
  }

  render() {
    const {posts, postId, post} = this.state;
    return (
      <div>
        <h1>Posts</h1>

        {/* 如果沒有傳 postID 進來時 */}
        {!postId && (
          <div className="container">
            <div className="row post-list">
              {posts.map(item => (
                <div className="post-item col-md-3 col-sm-6 mb-5" key={item.id} onClick={() => {
                  this.setState({
                    postId: item.id
                  })
                }}>
                  <div className="post-item__id">
                    {item.id}
                  </div>
                  {item.title}
                </div>
              ))}
            </div>
        </div>
      )}

      {/* 如果有傳 postID 進來時 */}
      { postId && (
        <div>
          <h2>{post.title}</h2>
          <p>
            {post.body}
          </p>
        </div>
      )}

    </div> 
    )
  }
}

export default Posts;
