import React, {Component} from 'react';
import {withRouter} from 'react-router-dom'; //Higher order component
import axios from 'axios';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
    }
  }

  componentDidMount() {
    axios.get('https://qootest.com/posts?_limit=6&_sort=id&_order=desc')
    .then(response => {
        this.setState({
          posts: response.data
        })
      })
  }

  render() {
    const {posts} = this.state;
    const {history} = this.props;

    return (
      <div>

        <div className="post-item col-md-12 mx-auto mt-3">
          <img src="https://images.unsplash.com/photo-1562332651-12481360937c?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1650&q=80" height="300px" width="100%" />
          <h2>New Features: </h2>
        </div>

        <div className="container mt-2">
          <div className="row post-list">     

              {posts.map((item)=> (
                <div className="post-item col-md-4  mt-3 mb-4" key={item.id} onClick={() => {
                  history.push('/post/' + item.id); //一個換頁的 function
                }}>
                  <h4>{item.title}</h4>
                  <div className="post-item__author">
                  Author: {item.author} 
                  </div>
                  <div className="post-item__body">
                  {item.body} 
                  </div>
                </div>
              ))}
          </div>
        </div>
      </div>
    )
  }
}

export default Home;
