import React, {Component} from 'react';
import './posts.css';

class Posts extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
      postId: null
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(response => response.json())
    .then(json => this.setState({
      posts: json
    }))
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.postId !== this.state.postId) {
      alert(this.state.postId);
      this.getPost(this.state.postId);
    }
  }

  getPost = id => {
    fetch('https://jsonplaceholder.typicode.com/posts' + this.state.postId)
    .then(response => response.json())
    .then(json => this.setState({
      posts: json
    }))
  }

  render() {
    const {posts} = this.state;
    return (
      <div>
        <h1>Posts</h1>
        <div className="container">
          <div className="row post-list">
            {posts.map(item => (
              <div className="post-item col-md-3 col-sm-6 mb-5" key={item.id} onClick={() => {
                this.setState({
                  postId: item.id
                })
              }}>
                <div className="post-item__id">
                  {item.id}
                </div>
                {item.title}
              </div>
            ))}
          </div>
        </div>
      </div>
    )
  }
}

export default Posts;
